INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'resilience', 'senior', 'low', 'Tell me about a time you missed a significant target. What did you do next?', 'Score 6: Candidate demonstrates radical accountability. They detail an objective root-cause analysis and how they communicated the shortfall early to buy-in for a recovery plan.'),
('behavioral', 'senior', 'senior', 'low', 'Describe a situation where a project didn’t deliver expected results.', 'Score 6: Focus on data-driven recovery. Candidate explains the gap between expectations and reality and the specific strategic adjustment made to salvaged value.'),
('behavioral', 'resilience', 'senior', 'low', 'How do you manage stress during high-performance periods?', 'Score 6: Executive presence. Candidate describes a system for delegation, technical compartmentalization, and maintaining a clear vision for the team despite pressure.'),
('behavioral', 'resilience', 'senior', 'low', 'Tell me about a challenging quarter and how you handled it.', 'Score 6: Strategic endurance. Candidate shows how they maintained pipeline health and team morale over a 90-day period of volatility.'),
('behavioral', 'resilience', 'senior', 'low', 'Describe a time when you had to manage repeated objections from clients.', 'Score 6: Persistence through insight. Candidate shows they didn''t just "push harder" but changed their discovery process to address the root systemic objection.'),
('behavioral', 'resilience', 'senior', 'low', 'How do you stay motivated during long sales cycles?', 'Score 6: Momentum management. Candidate defines how they create "micro-wins" and maintain stakeholder engagement over 6–12 month periods.'),
('behavioral', 'resilience', 'senior', 'low', 'Tell me about a time when you had to recover from a mistake.', 'Score 6: Professional integrity. Focus on immediate disclosure to leadership, a cost-saving fix, and a process change to automate the error out of the system.'),
('behavioral', 'resilience', 'senior', 'low', 'Describe a time when workload pressure tested you.', 'Score 6: Operational priority. Candidate describes using advanced prioritization (Paretto principle, etc.) to ensure high-impact results despite constraint.'),
('behavioral', 'resilience', 'senior', 'low', 'How do you respond when performance feedback is critical?', 'Score 6: Growth mindset. Candidate describes suppressing defensiveness, extracting the "truth" from the feedback, and a measurable shift in leadership behavior.'),
('behavioral', 'resilience', 'senior', 'low', 'Tell me about a time when persistence helped close a difficult opportunity.', 'Score 6: Creative persistence. Candidate identifies a "deadlock" and the specific, non-obvious angle they used to re-open and close the discussion.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'resilience', 'senior', 'medium', 'Tell me about a high-value deal that collapsed late in the cycle.', 'Score 6: Emotional maturity. Candidate describes the logical post-mortem, maintaining the relationship for the next cycle, and how they prevented it from affecting other active deals.'),
('behavioral', 'resilience', 'senior', 'medium', 'Describe a time when external market conditions impacted your results.', 'Score 6: Agility and ownership. Candidate shows they recognized the shift early and pivoted their positioning to find new relevance in the changed market.'),
('behavioral', 'resilience', 'senior', 'medium', 'How do you rebuild momentum after losing a strategic account?', 'Score 6: Resourcefulness. Candidate describes a structured plan to replace the revenue/impact via diversification or aggressive territory expansion.'),
('behavioral', 'resilience', 'senior', 'medium', 'Tell me about a time when leadership pressure affected your confidence.', 'Score 6: Self-regulation. Candidate describes how they managed the internal state and relied on their track record/data to communicate a logical timeline back to leadership.'),
('behavioral', 'resilience', 'senior', 'medium', 'Describe a situation where you had to keep your team motivated during setbacks.', 'Score 6: Visionary resilience. Candidate demonstrates how they provided transparent context while giving the team a concrete "re-entry plan" to follow.'),
('behavioral', 'resilience', 'senior', 'medium', 'How do you handle long periods without visible results?', 'Score 6: Leading indicators. Candidate focus on the "inputs" they controlled and how they verified their strategy was correct despite the delay in outcome.'),
('behavioral', 'resilience', 'senior', 'medium', 'Tell me about a time when you faced resistance from multiple stakeholders.', 'Score 6: Political resilience. Focus on identifying individual win-conditions and slowly moving a complex web of decision-makers toward one goal.'),
('behavioral', 'resilience', 'senior', 'medium', 'Describe a professional setback that changed your working approach.', 'Score 6: Structural evolution. A deep reflection on a failure that forced them to adopt a more scalable or risk-aware methodology.'),
('behavioral', 'resilience', 'senior', 'medium', 'When do you decide to disengage from an opportunity rather than persist?', 'Score 6: Strategic abandonment. Focus on the "Opportunity Cost" calculation and the courage to stop wasting company resources on a low-win-rate path.'),
('behavioral', 'resilience', 'senior', 'medium', 'Tell me about a time when emotional control was critical to maintaining performance.', 'Score 6: Composure. Describes a moment where reacting emotionally would have cost a deal or a team member, and how they stayed logical instead.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'resilience', 'senior', 'high', 'Tell me about a prolonged downturn in performance and how you turned it around.', 'Score 6: Turnaround leadership. Candidate identifies a systemic issue (market fit, process, skill gap) and describes the 6-month journey of fixing it.'),
('behavioral', 'resilience', 'senior', 'high', 'Describe a time when your strategic judgment was questioned.', 'Score 6: Conviction with evidence. Candidate shows they stood by a data-backed plan despite doubt, or had the humility to adjust when the data proved the doubters right.'),
('behavioral', 'resilience', 'senior', 'high', 'How do you handle repeated failure in a new market or territory?', 'Score 6: Iterative learning. Candidate describes "failing fast"—using each loss to build a more localized, accurate blueprint for success.'),
('behavioral', 'resilience', 'senior', 'high', 'Tell me about a time when your credibility was at risk.', 'Score 6: Radical candor. Focus on radical honesty, fixing the fallout personally, and using the crisis as a way to demonstrate higher reliability.'),
('behavioral', 'resilience', 'senior', 'high', 'Describe a period of burnout and how you managed it.', 'Score 6: Sustainable performance. Candidate identifies the structural causes of the burnout (not just "working hard") and the system changes they made to their leadership style to prevent it.'),
('behavioral', 'resilience', 'senior', 'high', 'How do you sustain resilience across multi-year sales cycles?', 'Score 6: Strategic stamina. Candidate describes managing multiple interpersonal relationships and technical hurdles over years without losing focus or quality.'),
('behavioral', 'resilience', 'senior', 'high', 'Tell me about a time when you had to lead through organizational instability.', 'Score 6: Stability anchor. Candidate demonstrates how they acted as a "buffer" for their team, maintaining focus on the mission while navigating the chaos of a re-org or acquisition.'),
('behavioral', 'resilience', 'senior', 'high', 'Describe a failure that reshaped your leadership style.', 'Score 6: Ego dissolution. Candidate shows a fundamental shift from "Individual Hero" to "Team Enabler" as a result of a major project loss.'),
('behavioral', 'resilience', 'senior', 'high', 'How do you differentiate between temporary setbacks and systemic problems?', 'Score 6: Analytical framework. Candidate describes the specific thresholds or metrics they use to decide if a strategy needs "more time" or a "total overhaul."'),
('behavioral', 'resilience', 'senior', 'high', 'Tell me about a time when resilience meant stepping back instead of pushing forward.', 'Score 6: Advanced wisdom. Focus on the maturity to recognize when a team or strategy is at a breaking point and the decision to "regroup" to win later.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'communication', 'senior', 'low', 'Tell me about presenting to mid-level management.', 'Score 6: Audience alignment. Candidate shows they translated technical or day-to-day data into "Strategic KPIs" that mid-level managers care about.'),
('behavioral', 'communication', 'senior', 'low', 'Describe resolving a communication breakdown.', 'Score 6: Restoration. Candidate identifies the "noise" in the system (wrong channel, lack of context) and describes the specific meeting/tool used to clear it up.'),
('behavioral', 'communication', 'senior', 'low', 'How do you manage objections professionally?', 'Score 6: Consultative composure. Candidate describes "isolating the objection" and using evidence to address it without becoming defensive.'),
('behavioral', 'communication', 'senior', 'low', 'Tell me about explaining strategy to your team.', 'Score 6: Translation. Focus on breaking a large corporate goal into "Actionable Steps" for the team, ensuring every member knows their "Why."'),
('behavioral', 'communication', 'senior', 'low', 'Describe maintaining clarity in complex discussions.', 'Score 6: Facilitation. Candidate describes using summaries, visual aids, or frequent "alignment checks" to ensure everyone is on the same page.'),
('behavioral', 'communication', 'senior', 'low', 'Tell me about communicating performance feedback.', 'Score 6: Constructive impact. Candidate shows they used a "Feedback Loop" approach—identifying the gap but focusing 80% of the conversation on the solution.'),
('behavioral', 'communication', 'senior', 'low', 'How do you ensure alignment across departments?', 'Score 6: Boundary spanning. Mentions specific mechanisms (cross-functional syncs, shared docs) used to ensure no one is working in a silo.'),
('behavioral', 'communication', 'senior', 'low', 'Describe handling a dissatisfied client.', 'Score 6: De-escalation. Candidate demonstrates active listening to the frustration before moving into a "Solution Partner" mode.'),
('behavioral', 'communication', 'senior', 'low', 'Tell me about maintaining credibility in discussions.', 'Score 6: Evidence-based authority. Candidate shows how they use data, external benchmarks, or direct experience to ground their arguments in reality.'),
('behavioral', 'communication', 'senior', 'low', 'How do you adjust tone in sensitive conversations?', 'Score 6: Empathy logic. Candidate describes reading the emotional context and intentionally choosing a slower pace or more collaborative language.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'communication', 'senior', 'medium', 'Tell me about influencing senior stakeholders.', 'Score 6: Executive alignment. Candidate speaks about "Business Case" language (ROI, Risk, Opportunity Cost) used to convince C-level or VPs.'),
('behavioral', 'communication', 'senior', 'medium', 'Describe a high-stakes negotiation.', 'Score 6: Strategic trade-offs. Candidate details the planning phase, their walk-away points, and the "Value Expansion" they used to close the deal.'),
('behavioral', 'communication', 'senior', 'medium', 'How do you communicate long-term strategy?', 'Score 6: Narrative skill. Candidate shows they can paint a compelling future while grounding it in immediate, achievable milestones.'),
('behavioral', 'communication', 'senior', 'medium', 'Tell me about managing communication during change.', 'Score 6: Constant cadence. Focus on the frequency and transparency of messaging to reduce the "rumor mill" and maintain team buy-in during a shift.'),
('behavioral', 'communication', 'senior', 'medium', 'Describe preventing escalation through communication.', 'Score 6: Proactive diplomacy. Candidate identifies a potential fallout and has the "hard conversation" early to resolve it before it reaches leadership.'),
('behavioral', 'communication', 'senior', 'medium', 'Tell me about aligning multiple decision-makers.', 'Score 6: Consensus orchestration. Candidate describes identifying the "silent objector" and addressing their concerns privately to ensure a smooth public rollout.'),
('behavioral', 'communication', 'senior', 'medium', 'How do you balance transparency and diplomacy?', 'Score 6: Professional nuance. Candidate shows they can deliver the "Whole Truth" in a way that is constructive rather than destructive to the business.'),
('behavioral', 'communication', 'senior', 'medium', 'Describe persuading resistant stakeholders.', 'Score 6: Objection mapping. Candidate illustrates how they used the stakeholder''s own goals to show how the new strategy would help *them* succeed.'),
('behavioral', 'communication', 'senior', 'medium', 'Tell me about rebuilding damaged relationships.', 'Score 6: Trust recovery. Focus on the long-term consistency of actions matching words, and a specific "apology-plus-solution" moment.'),
('behavioral', 'communication', 'senior', 'medium', 'How do you communicate during uncertain situations?', 'Score 6: Stability in communication. Candidate shows they communicated "What we know" and "When we will know more," providing a sense of control despite the fog.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'communication', 'senior', 'high', 'Tell me about influencing without formal authority.', 'Score 6: Social capital. Candidate explains how they used rapport, expertise, and reciprocity to move a project forward using others over whom they had no rank.'),
('behavioral', 'communication', 'senior', 'high', 'Describe handling board-level discussions.', 'Score 6: High-altitude impact. Focus on brevity, presenting "Strategic Options" rather than "Updates," and the ability to defend a position under pressure.'),
('behavioral', 'communication', 'senior', 'high', 'How do you communicate unpopular decisions?', 'Score 6: Logical courage. Candidate shows they prioritized the "Outcome for the many" over "Likability by the few," providing the full rationale behind the decision.'),
('behavioral', 'communication', 'senior', 'high', 'Tell me about managing communication in crisis.', 'Score 6: Crisis coordination. Candidate describes acting as a single, calm source of truth, managing stakeholder anxiety while directing technical recovery.'),
('behavioral', 'communication', 'senior', 'high', 'Describe defending a strategy under scrutiny.', 'Score 6: Fact-based resilience. Candidate shows they welcomed the scrutiny and used it to prove the strategy''s depth, rather than becoming defensive.'),
('behavioral', 'communication', 'senior', 'high', 'How do you handle media or public-facing communication?', 'Score 6: Narrative control. Candidate shows they can stay "On-Message" while addressing difficult questions, protecting the company''s reputation.'),
('behavioral', 'communication', 'senior', 'high', 'Tell me about influencing across cultures.', 'Score 6: Cultural intelligence. Candidate describes researching and adjusting their tone, pacing, and relationship-building style for global effectiveness.'),
('behavioral', 'communication', 'senior', 'high', 'Describe resolving political tension through communication.', 'Score 6: Neutral mediation. Candidate shows they acted as a "Bridge," identifying the ego-drivers and moving everyone back to the project goal.'),
('behavioral', 'communication', 'senior', 'high', 'How do you mentor others in advanced communication?', 'Score 6: Multiplier effect. Candidate demonstrates they can teach others how to read a room, handle objections, or structure a high-stakes pitch.'),
('behavioral', 'communication', 'senior', 'high', 'Tell me about shaping organizational narrative.', 'Score 6: Visionary impact. Focus on how they helped define "Who we are" and "How we work," influencing the collective identity of the business.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'adaptability', 'senior', 'low', 'Tell me about adjusting to new senior leadership.', 'Score 6: Strategic alignment. Candidate shows they proactively studied the new leader''s priorities and adjusted their own department''s roadmap to match.'),
('behavioral', 'adaptability', 'senior', 'low', 'Describe adapting to client expectation changes.', 'Score 6: Client agility. Candidate describes re-negotiating the scope and resources to meet the new expectation while protecting their team''s output.'),
('behavioral', 'adaptability', 'senior', 'low', 'How do you handle mid-cycle target revisions?', 'Score 6: Tactical pivot. Candidate shows they immediately recalculated their lead-gen or activity metrics to hit the harder target without panic.'),
('behavioral', 'adaptability', 'senior', 'low', 'Tell me about managing evolving team dynamics.', 'Score 6: Social adaptivity. Candidate describes how they changed their leadership style (e.g., from hands-on to coaching) as the team matured.'),
('behavioral', 'adaptability', 'senior', 'low', 'Describe adapting to regulatory changes.', 'Score 6: Compliance agility. Candidate shows they viewed the new regulation as a "New Constraint" and worked within it to find a competitive advantage.'),
('behavioral', 'adaptability', 'senior', 'low', 'Tell me about shifting account strategies.', 'Score 6: Hypothesis testing. Candidate describes the "Pivot Point" where a strategy wasn''t working and the logical step into a new market or messaging.'),
('behavioral', 'adaptability', 'senior', 'low', 'How do you respond to internal process changes?', 'Score 6: Process pragmatism. Candidate demonstrates they adopted the new system quickly and became an early advocate to help others move faster.'),
('behavioral', 'adaptability', 'senior', 'low', 'Describe adapting during high-growth phases.', 'Score 6: Scalability mindset. Candidate shows they recognized when old, manual processes were breaking and pivoted to automated or delegatory systems.'),
('behavioral', 'adaptability', 'senior', 'low', 'Tell me about entering a new territory or market.', 'Score 6: Learning agility. Focus on the rapid research phase and how they unlearned their previous assumptions to fit the new territory''s reality.'),
('behavioral', 'adaptability', 'senior', 'low', 'How do you handle working across different industries?', 'Score 6: Domain agility. Candidate shows they can extract "Universal Principles" from one industry and apply them creatively to another.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'adaptability', 'senior', 'medium', 'Tell me about pivoting during market disruption.', 'Score 6: Disruption response. Candidate describes a "Defense-to-Offense" shift—finding an opportunity in a crisis that competitors missed.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Describe adapting to long enterprise sales cycles.', 'Score 6: Stakeholder adaptivity. Candidate demonstrates they changed their communication and multi-threaded their approach as the deal complexity increased.'),
('behavioral', 'adaptability', 'senior', 'medium', 'How do you respond to aggressive competitors?', 'Score 6: Competitive pivot. Candidate shows they didn''t just "Discount," but adapted their value prop to highlight the competitor''s newly discovered weakness.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Tell me about restructuring a failing account plan.', 'Score 6: Root-cause pivot. Candidate identifies why the previous plan failed (wrong person, wrong message) and details the total redesign of the account.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Describe managing change fatigue within your team.', 'Score 6: Empathetic adaptivity. Candidate describes slowing down non-essential changes and focusing the team on "Meaningful Progress" to restore morale.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Tell me about adapting to new pricing models.', 'Score 6: Value-sell adaptivity. Candidate shows they discarded old "Feature-based" selling to adopt a "Value-based" conversation required by the new model.'),
('behavioral', 'adaptability', 'senior', 'medium', 'How do you handle technological disruption?', 'Score 6: Digital agility. Candidate shows they embraced a disruptive tool (like AI or a new CRM) to leapfrog a previous manual bottle-neck.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Describe adjusting strategy after data insights.', 'Score 6: Data-led humility. Candidate shows they were willing to kill a "Pet Project" when the data surfaced a more effective alternative.'),
('behavioral', 'adaptability', 'senior', 'medium', 'Tell me about adapting to shifting buyer behavior.', 'Score 6: Buyer-journey agility. Candidate describes moving from "Cold Outreach" to "Content/Social Selling" as they noticed the market becoming more research-heavy.'),
('behavioral', 'adaptability', 'senior', 'medium', 'How do you maintain agility while scaling operations?', 'Score 6: Balanced growth. Candidate shows they built "Agile Guardrails"—processes that allow for freedom and speed without losing total control across a larger team.');
INSERT INTO public.assessment_questions (category, driver, experience_band, difficulty, question_text, evaluation_rubric) VALUES
('behavioral', 'adaptability', 'senior', 'high', 'Tell me about leading change across multiple teams.', 'Score 6: Cross-departmental orchestration. Focus on how they unified different cultures and incentives around a single, massive shift in direction.'),
('behavioral', 'adaptability', 'senior', 'high', 'Describe adapting during merger or acquisition.', 'Score 6: Integration agility. Candidate shows they looked for the "Best of Both" cultures and adapted their own team''s workflow to accelerate the merger.'),
('behavioral', 'adaptability', 'senior', 'high', 'How do you respond to systemic business failure?', 'Score 6: Survival-to-Success shift. Candidate describes a "Triage" moment where they cut failing branches to save the trunk and grow in a new, healthier direction.'),
('behavioral', 'adaptability', 'senior', 'high', 'Tell me about redesigning strategy due to industry shifts.', 'Score 6: Visionary pivot. Candidate describes a fundamental change in "Business Model" (e.g., product-to-service) to remain relevant for the next decade.'),
('behavioral', 'adaptability', 'senior', 'high', 'Describe managing global market changes.', 'Score 6: Geo-political adaptivity. Candidate shows they anticipated a macro shift (trade war, currency shift) and hedged their territory strategy accordingly.'),
('behavioral', 'adaptability', 'senior', 'high', 'How do you adapt leadership style over time?', 'Score 6: Personal evolution. Candidate describes moving from "Command-and-Control" to "Servant-Leadership" as they learned to scale through others.'),
('behavioral', 'adaptability', 'senior', 'high', 'Tell me about making unpopular but necessary adjustments.', 'Score 6: Conviction-based agility. Candidate shows they prioritized the long-term survival of the business over immediate personal comfort or team popularity.'),
('behavioral', 'adaptability', 'senior', 'high', 'Describe adapting to geopolitical or macroeconomic risks.', 'Score 6: Macro-flexibility. Candidate demonstrates they had "Plan B and C" ready for when the external world changed beyond their control.'),
('behavioral', 'adaptability', 'senior', 'high', 'How do you handle cultural transformation initiatives?', 'Score 6: Identity adaptivity. Focus on how they helped a "Legacy" team adopt a "Modern/Agile" mindset through consistent modeling and small, rapid wins.'),
('behavioral', 'adaptability', 'senior', 'high', 'Tell me about future-proofing your business strategy.', 'Score 6: Anticipatory agility. Candidate describes investing time/resources in "Horizon 2 and 3" activities while the current "Horizon 1" was still successful.');
